import { userConstants } from '../constants';
import { alertActions } from './';
import fire from "../firebase";

export const dataActions = {
  populateDashboard,
};

function populateDashboard() {
  return dispatch => {
    var locations;
    fire.database().ref('/lockers/data').once('value', function (snapshot) {
      let tempArray = {};
      snapshot.forEach(function (childSnapshot) {
        var childData = childSnapshot.val();
        tempArray[childSnapshot.key] = childData;
      });
      locations = tempArray;
      dispatch(mealsSuccess(tempArray));
    }.bind(this));
    console.log(locations);

    var mealsForSale;
    fire.database().ref('/meals/forSale').once('value', function (snapshot) {
      let tempArray = {};
      snapshot.forEach(function (childSnapshot) {
        var childData = childSnapshot.val();
        tempArray[childSnapshot.key] = childData;
      });
      mealsForSale = tempArray;
    }, function (error) {
      if (error) {
        // The write failed...
      } else {
        // Data saved successfully!
      }
    });
    console.log(mealsForSale);

    var pickedUp;
    fire.database().ref('/restaurants/' + org + '/inventory/pickedUp').once('value', function (snapshot) {
      let tempArray = {};
      snapshot.forEach(function (childSnapshot) {
        var childData = childSnapshot.val();
        tempArray[childSnapshot.key] = childData;
      });
      pickedUp = tempArray;
    }.bind(this));
    console.log(pickedUp);

    fire.auth()
      .signInWithEmailAndPassword(email, password)
      .then(user => {
        var tempUser = {};
        if (user) {
          fire.database().ref('/users/' + user.user.uid).once('value').then(function (snapshot) {
            tempUser["name"] = snapshot.val().name;
            tempUser["headshot"] = snapshot.val().headshot;
            tempUser["phone"] = snapshot.val().phone;
            tempUser["org"] = snapshot.val().org;
            localStorage.setItem('user', JSON.stringify(user.valueOf()));
            dispatch(success(user, tempUser));
            history.push('/');
          },
            error => {
              localStorage.setItem('user', null);
              dispatch(failure(error.toString()));
              dispatch(alertActions.error(error.toString()));
            });
          dispatch(request({ email }));
        }
      },
        error => {
          localStorage.setItem('user', null);
          dispatch(failure(error.toString()));
          dispatch(alertActions.error(error.toString()));
        });


    var user = JSON.parse(localStorage.getItem('user'));
    console.log(user);
  };

  function mealsRequest(user) { return { type: userConstants.UPDATE_MEALS_REQUEST, user } }
  function mealsSuccess(user, userData) { return { type: userConstants.UPDATE_MEALS_SUCCESS, user, userData } }
  function mealsFailure(error) { return { type: userConstants.UPDATE_MEALS_FAILURE, error } }
  function locationsRequest(user) { return { type: userConstants.UPDATE_LOCATIONS_REQUEST, user } }
  function locationsSuccess(user, userData) { return { type: userConstants.UPDATE_LOCATIONS_SUCCESS, user, userData } }
  function locationsFailure(error) { return { type: userConstants.UPDATE_LOCATIONS_FAILURE, error } }
  function pickedUpRequest(user) { return { type: userConstants.UPDATE_PICKEDUP_REQUEST, user } }
  function pickedUpSuccess(user, userData) { return { type: userConstants.UPDATE_PICKEDUP_SUCCESS, user, userData } }
  function pickedUpFailure(error) { return { type: userConstants.UPDATE_PICKEDUP_FAILURE, error } }
  };
