import { userConstants } from '../constants';
import { alertActions } from './';
import fire from "../firebase";

export const userActions = {
    login,
    logout,
    //register,
    //delete: _delete
};

function login(email, password) {
  return dispatch => {
    fire.auth()
      .signInWithEmailAndPassword(email, password)
      .then(user => {
        if (user) {
          fire.database().ref('/users/' + user.user.uid).once('value').then(function (snapshot) {
            user.user["name"] = snapshot.val().name;
            user.user["headshot"] = snapshot.val().headshot;
            user.user["phone"] = snapshot.val().phone;
            user.user["org"] = snapshot.val().org;
            localStorage.setItem('user', JSON.stringify(user));
            dispatch(success(user));
            return user;
          },
            error => {
              localStorage.setItem('user', null);
              dispatch(failure(error.toString()));
              dispatch(alertActions.error(error.toString()));
            });
          dispatch(request({ email }));
        }
      },
      error => {
          localStorage.setItem('user', null);
          dispatch(failure(error.toString()));
          dispatch(alertActions.error(error.toString()));
        });
  };

  function request(user) { return { type: userConstants.LOGIN_REQUEST, user } }
  function success(user) { return { type: userConstants.LOGIN_SUCCESS, user } }
  function failure(error) { return { type: userConstants.LOGIN_FAILURE, error } }
}

function logout(firebase) {
    fire.auth().signOut();
    return { type: userConstants.LOGOUT };
}

