import React, { Component } from 'react';
import RGL, { WidthProvider } from "react-grid-layout";
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
const ReactGridLayout = WidthProvider(RGL);
const _ = require('lodash');


export default class Locations extends Component {
  constructor(props) {
    super(props);
    this.state = {
      layout: null,
      locations: null,
      org: null,
      mealsForSale: null,
      uid: null,
    };
  }

  async componentDidMount() {
    const layout = this.generateLayout();
    this.setState({ layout });
  }

  generateDOM() {
    var temp = Object.values(JSON.parse(this.props.locationsData));
    return _.map(_.range(temp.length), function (i) {
      return (
        <div key={i.toString()}>
          <Card style={{maxWidth: 345, height: 400}}>
            <CardActionArea>
              <CardMedia
                component="img"
                alt=""
                style={{ objectFit: 'cover', aspectRatio: 1 }}
                height="300"
                image={temp[i].image}
                title={temp[i].title}
              />
              <CardContent>
                <div className="center">
                  <Typography component="p" variant="caption">
                    {temp[i].title}
                  </Typography>
                </div>
                <div className="center">
                  <Typography component="p" variant="caption">
                    {temp[i].title}
                  </Typography>
                </div>
              </CardContent>
            </CardActionArea>
          </Card>
        </div>
      );
    });
  }

  generateLayout() {
    var temp = Object.values(JSON.parse(this.props.locationsData));
    return _.map(new Array(temp.length), function (item, i) {
      const y = 7;
      return {
        x: (i * 6) % 12,
        y: Math.floor(i / 7) * y,
        w: 6,
        h: y,
        i: i.toString(),
        static: true
      };
    });
  }

  onLayoutChange(layout) {
    this.props.onLayoutChange(layout);
  }

  render() {
    if (this.props.locationsData && this.state.layout) {
      return (
        <ReactGridLayout
          layout={this.state.layout}
          onLayoutChange={this.onLayoutChange}
          {...this.props}
        >
          {this.generateDOM()}
        </ReactGridLayout>
      );
    }
    return <div>Loading...</div>;
  }
}

Locations.defaultProps = {
  className: "layout",
  items: 20,
  rowHeight: 30,
  onLayoutChange: function () { },
  cols: 12
};
