import React, { Component } from 'react';
const dateformat = require('dateformat');

export default class ActiveOrders extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orders: null,
      org: null,
      mealsForSale: null,
      uid: null,
      total: null,
    };
  }

  processData() {
    var locations = {};
    var mealsForSale = {};

    for (var i in this.props.meals) {
      if (this.props.mealsForSale[this.props.meals[i].idMeal]) {
        mealsForSale[this.props.meals[i].idMeal] = this.props.meals[i];
      }
    }

    // First value is location data, second is how many meals in locker, third is purchased meals from locker, fourth is completed transactions
    for (i in this.props.locationsData) {
      locations[this.props.locationsData[i].type] = [this.props.locationsData[i], 0, 0, 0];
    }

    for (i in mealsForSale) {
      locations[mealsForSale[i].location.toLowerCase()][1] += 1
    }


    for (i in this.props.purchased) {
      locations[this.props.purchased[i].location.toLowerCase()][2] += 1;
      if (i in this.props.completed) {
        purchasedNotCompleted[i] = this.props.purchased[i];
      }
    }

    this.setState({ purchasedNotCompleted });

    for (i in this.props.completed) {
      locations[this.props.completed[i].location.toLowerCase()][3] += 1
    }

    return locations;
  }

  async componentDidMount() {
    var orders = this.processData();
    this.setState({ orders });
  }

  renderItems() {
    return (
      this.state.orders.map((order, index) =>
        <tr key={index}>
          <td className="fw-600">{order.strMeal}</td>
          <td><span>{order.name}</span> </td>
          <td>{order.strDatePurchased.slice(order.strDatePurchased.lastIndexOf(',') + 1, order.strDatePurchased.length)}</td>
          <td><span className="text-info">${order.strCost}</span></td>
        </tr>
      )
    );
  }

  render() {
    var date = Date.now();
    var d = dateformat(date, 'dddd, mmmm d, yyyy');
    if (this.state.orders) {
      return (
        <div>
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th className=" bdwT-0">Name</th>
                  <th className=" bdwT-0">Customer</th>
                  <th className=" bdwT-0">Time</th>
                  <th className=" bdwT-0">Price</th>
                </tr>
              </thead>
              <tbody>
                {this.renderItems()}
              </tbody>
            </table>
            <div className="check-container">
              <a href="http://fudlkr.com" className="check-text">Check all the sales</a>
            </div>
          </div>
        </div>
      );
    }
    return <div>Loading...</div>;
  }
}
