import React, { Component } from 'react';
import { connect } from 'react-redux';
import { dataActions } from '../../actions';
const _ = require('lodash');

class AddMealPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      dropdownOpen: false,
      radioSelected: 2,
      active: false,
      locations: null,
    };
  }

  async componentDidMount() {
    const { dispatch, user, userData } = this.props;
    var locations = Object.values(localStorage.getItem('locations'));
    console.log(localStorage.getItem('locations'));
    console.log(locations);
    if (locations == null) {
      await dispatch(dataActions.populateAddMeal());
      locations = Object.values(localStorage.getItem('locations'));
    }
    this.setState({ locations });
    this.generateOptions();
  }

  generateOptions() {
    var select = document.querySelector('#locations');
    _.map(_.range(this.state.locations.length), function (i) {
      var option = document.createElement("option");
      option.text = this.state.locations[i].title;
      select.add(option);
    });
  }

  activeClicked() {
    this.setState({ active: !this.state.active });
    var active = document.querySelector('#active');
    var activeUntil = document.querySelector('#activeUntil');
    active.checked = this.state.active;
    activeUntil.hidden = !this.state.active;
  }


  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

  render() {
    if (this.state.locations) {
      return (
        <div className="masonry-item col-md-12">
          <div className="bgc-white p-20 bd">
            <h4 className="c-grey-900 mT-10 mB-30">Add a New Meal</h4>
            <div className="d-flex justify-content-center align-items-center container pt-3">
              <input type="image" id="food-image" alt="food-pic" src="http://fudlkr.com/mobile_assets/green.png" width="324" height="228" />
            </div>
            <div className="d-flex justify-content-center align-items-center container pt-3">
              <button type="submit" className="btn btn-secondary">Upload</button>
            </div>
            <div className="mT-30">
              <form className="form-container">
                <div className="form-row">
                  <div className="form-group col-md-12">
                    <label htmlFor="meal_desc">Meal Name</label>
                    <input type="text" className="form-control" id="meal_desc" placeholder="Chicken Bacon Ranch Pizza" />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group col-md-12">
                    <label htmlFor="meal_desc">Meal Description</label>
                    <input type="text" className="form-control" id="meal_desc" placeholder="Asiago Chicken, Farm Raised Bacon, Hidden Valley Ranch, etc." />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label htmlFor="cost">Estimated Calories</label>
                    <input type="number" min="10" step="any" className="form-control" id="calories" placeholder="350" />
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="meal_cat1">Meal Category</label>
                    <select id="meal_cat1" className="form-control" defaultValue="Choose...">
                      <option>American</option>
                      <option>Appetizers</option>
                      <option>Asian</option>
                      <option>Italian</option>
                      <option>Mexican</option>
                      <option>Salads</option>
                    </select>
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="meal_cat2">Location</label>
                    <select id="locations" className="form-control" defaultValue="Choose...">
                    </select>
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label htmlFor="cost">Cost</label>
                    <input type="number" min="1" step="any" className="form-control" id="cost" placeholder="$3.50" />
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="cost">Default Net Weight (oz)</label>
                    <input type="number" max="100" step="any" className="form-control" id="weight" placeholder="4.21" />
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="shelf_life">Shelf Life (days)</label>
                    <input type="number" min="0.25" max="14" step="any" className="form-control" id="shelf_life" placeholder="2.5" />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group">
                    <div className="checkbox checkbox-circle checkbox-info peers ai-c">
                      <input type="checkbox" id="active" name="active" className="peer" onClick={() => { this.activeClicked() }} />
                      <label htmlFor="active" className="peers peer-greed js-sb ai-c">
                        <span className="peer peer-greed">Active</span>
                      </label>
                    </div>
                  </div>
                </div>
                <div id="activeUntil" hidden className="form-row">
                  <div className="form-group col-md-6">
                    <label className="fw-500">Active Until</label>
                    <div className="timepicker-input input-icon form-group">
                      <div className="input-group">
                        <div className="input-group-addon bgc-white bd bdwR-0">
                          <i className="ti-calendar"></i>
                        </div>
                        <input type="text" className="form-control bdc-grey-200 start-date" placeholder="Pick a Date" data-provide="datepicker" />
                      </div>
                    </div>
                  </div>
                </div>
                <button type="submit" className="form-row center btn btn-primary">Add Meal to Inventory</button>
              </form>
            </div>
          </div>
        </div>
      );
    }
    else {
      return null;
    }
  }
}

function mapStateToProps(state) {
  const { authentication, data } = state;
  const { user, userData } = authentication;
  const { restaurantData } = data;
  return {
    user,
    userData,
    restaurantData
  };
}

const AddMeal = connect(mapStateToProps)(AddMealPage);
export default AddMeal;
