import React, { Component } from 'react';
import RGL, { WidthProvider } from "react-grid-layout";
const ReactGridLayout = WidthProvider(RGL);
import { connect } from 'react-redux';

class LocationsComponent extends Component {
  constructor(props) {
    super(props);
    const layout = this.generateLayout();
    this.state = {
      layout,
      locations: null,
      org: null,
      mealsForSale: null,
      uid: null,
    };
  }

  async componentDidMount() {
    console.log(this.props.restaurantData);
  }

  generateDOM() {
    return _.map(_.range(this.props.items), function (i) {
      return (
        <div key={i}>
          <span className="text">{i}</span>
        </div>
      );
    });
  }

  generateLayout() {
    const p = this.props;
    return _.map(new Array(p.items), function (item, i) {
      const y = _.result(p, "y") || Math.ceil(Math.random() * 4) + 1;
      return {
        x: (i * 2) % 12,
        y: Math.floor(i / 6) * y,
        w: 2,
        h: y,
        i: i.toString()
      };
    });
  }

  onLayoutChange(layout) {
    this.props.onLayoutChange(layout);
  }

  render() {
    if (this.state.locations) {
      return (
        <ReactGridLayout
          layout={this.state.layout}
          onLayoutChange={this.onLayoutChange}
          {...this.props}
        >
          {this.generateDOM()}
        </ReactGridLayout>
      );
    }
    return <div>Loading...</div>;
  }
}

LocationsComponent.defaultProps = {
  className: "layout",
  items: 20,
  rowHeight: 30,
  onLayoutChange: function () { },
  cols: 12
};

function mapStateToProps(state) {
  const { authentication, data } = state;
  const { user, userData } = authentication;
  const { restaurantData } = data;
  return {
    user,
    userData,
    restaurantData
  };
}

const Locations = connect(mapStateToProps)(LocationsComponent);
export default Locations;
