import React, { Component } from 'react';
import { connect } from 'react-redux';
import { dataActions } from '../../actions';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import fire from "../../firebase";
import { FilePond, File, registerPlugin } from "react-filepond";
import "filepond/dist/filepond.min.css";
import FilePondPluginImageExifOrientation from 'filepond-plugin-image-exif-orientation';
import FilePondPluginImagePreview from 'filepond-plugin-image-preview';
import 'filepond-plugin-image-preview/dist/filepond-plugin-image-preview.css';
import FilePondPluginFileValidateType from 'filepond-plugin-file-validate-type';
const _ = require('lodash');
const dateformat = require('dateformat');

registerPlugin(FilePondPluginImageExifOrientation, FilePondPluginImagePreview, FilePondPluginFileValidateType)

class AddMealPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      dropdownOpen: false,
      radioSelected: 2,
      active: false,
      locations: null,
      startDate: new Date(),
      mealData: null,
      pic: "http://fudlkr.com/mobile_assets/green.png",
      // Set initial files, type 'local' means this is a file
      // that has already been uploaded to the server (see docs)
      files: []
    };
    this.activeClicked = this.activeClicked.bind(this);
    this.datePickerChange = this.datePickerChange.bind(this);
  }

  async componentDidMount() {
    const { dispatch, user, userData } = this.props;
    var locations = Object.values(JSON.parse(localStorage.getItem('locations')));
    if (locations == null) {
      await dispatch(dataActions.populateAddMeal());
      locations = Object.values(localStorage.getItem('locations'));
    }
    await this.setState({ locations });
    this.generateOptions(locations);
  }

  generateOptions(locations) {
    var select = document.querySelector('#locations');
    _.map(_.range(locations.length), function (i) {
      var option = document.createElement("option");
      option.text = locations[i].title;
      select.add(option);
    });
  }

  activeClicked() {
    this.setState({ active: !this.state.active });
    var active = document.querySelector('#active');
    var activeUntil = document.querySelector('#activeUntil');
    active.checked = this.state.active;
    activeUntil.hidden = !this.state.active;
  }

  datePickerChange(date) {
    this.setState({
      startDate: date
    });
  }

  validateData() {
    var mealTitle = document.forms["form"]["meal_name"].value;
    var mealDesc = document.forms["form"]["meal_desc"].value;
    var calories = document.forms["form"]["calories"].value;
    var mealCat = document.forms["form"]["meal_cat"].value;
    var location = document.forms["form"]["locations"].value;
    var cost = document.forms["form"]["cost"].value;
    var weight = document.forms["form"]["weight"].value;
    var shelfLife = document.forms["form"]["shelf_life"].value;

    if (mealTitle === "") {
      alert("Meal Title must be filled out");
      document.forms["form"]["meal_name"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["meal_name"].style.borderColor = "green";
    if (mealDesc === "") {
      alert("Meal Description must be filled out");
      document.forms["form"]["meal_desc"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["meal_desc"].style.borderColor = "green";
    if (calories === "" || isNaN(calories)) {
      alert("Invalid entry for estimated calories! Please provide a number between 10-2000.");
      document.forms["form"]["calories"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["calories"].style.borderColor = "green";
    if (mealCat === "Choose...") {
      alert("Meal Category must be filled out");
      document.forms["form"]["meal_cat"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["meal_cat"].style.borderColor = "green";
    if (location === "Choose...") {
      alert("Location must be filled out");
      document.forms["form"]["locations"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["locations"].style.borderColor = "green";
    if (cost === "" || isNaN(cost)) {
      alert("Invalid entry for cost! Please provide a number.");
      document.forms["form"]["cost"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["cost"].style.borderColor = "green";
    if (weight === "" || isNaN(weight)) {
      alert("Invalid entry for weight! Please provide a number.");
      document.forms["form"]["weight"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["weight"].style.borderColor = "green";
    if (shelfLife === "" || isNaN(shelfLife)) {
      alert("Invalid entry for the shelf life! Please provide a number between 0.25 and 14.");
      document.forms["form"]["shelf_life"].style.borderColor = "red";
      return false;
    }
    document.forms["form"]["shelf_life"].style.borderColor = "green";
    this.setState({
      mealData: {

      }})
    return true;
  }

  async addNewMeal() {
    var userId = fire.auth().currentUser.uid;
    this.validateData();
    return;
    var mealData = this.state.mealData;
    if (mealData == null) {
      await this.formatData();
      mealData = this.state.mealData;
    }
    var date = Date.now();
    var d = dateformat(date, 'dddd, mmmm d, yyyy, h:MM:ss TT');
    mealData["strDatePackaged"] = d.toString();
    mealData["datePackaged"] = date.toString();
    await fire.database().ref('/restaurants/' + this.state.org + '/mealCounter').once('value').then(function (snapshot) {
      mealData["idMeal"] = snapshot.val();
    }.bind(this));
    await fire.database().ref('/restaurants/' + this.state.org + '/').update({
      mealCounter: mealData.idMeal + 1
    });
    mealData["idMeal"] = "0" + this.state.idOrg + "" + mealData["idMeal"];
    mealData["strIdMeal"] = "Meal ID #" + mealData["idMeal"];
    mealData["strMeal"] = this.state.mealTitle;
    mealData["forSale"] = true;
    if (this.state.initialPicURI != this.state.files[0]) {
      const blob = await new Promise((resolve, reject) => {
        const xhr = new XMLHttpRequest();
        xhr.onload = function () {
          resolve(xhr.response);
        };
        xhr.onerror = function (e) {
          console.log(e);
          reject(new TypeError('Network request failed'));
        };
        xhr.responseType = 'blob';
        xhr.open('GET', this.state.files[0].file, true);
        xhr.send(null);
      });
      if (this.state.imageUrl == null) {
        var picRef = fire.storage().ref('/meals/').child(mealData["idMeal"] + '.jpeg ');
        await picRef.put(blob).then(function (snapshot) { });
        await picRef.getDownloadURL().then(function (url) {
          this.setState({ imageUrl: url })
        }.bind(this));
        mealData["strMealThumb"] = this.state.imageUrl;
      }
    }
    else {
      mealData["strMealThumb"] = this.state.initialPicURI;
    }

    // upload data to firebase
    await fire.database().ref('/meals/all/meals/' + this.state.mealData["idMeal"] + '/').push(mealData);
    await fire.database().ref('/meals/categories/' + mealData['strCategory'].toLowerCase() + '/meals/' + this.state.mealData["idMeal"] + '/').push(mealData);
    await fire.database().ref('/meals/locations/' + mealData['location'].toLowerCase() + '/meals/' + this.state.mealData["idMeal"] + '/').push(mealData);
    await fire.database().ref('/restaurants/' + mealData['distributor'].toLowerCase() + '/meals/history/' + this.state.mealData["location"].toLowerCase() + '/' + this.state.mealData["idMeal"] + '/').push(mealData);
    var forSale = {};
    forSale[mealData.idMeal] = true;
    await fire.database().ref('/meals/forSale/').update(forSale);
    alert("Meal Added to Location Successfuly!");
  }

  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

  render() {
    if (this.state.locations) {
      return (
        <div className="masonry-item col-md-12">
          <div className="bgc-white p-20 bd">
            <h4 className="c-grey-900 mT-10 mB-30">Add a New Meal</h4>
            <FilePond
              allowMultiple={true}
              allowFileTypeValidation={true}
              acceptedFileTypes={['image/*']}
              fileValidateTypeDetectType={(source, type) => new Promise((resolve, reject) => {
                resolve(type);
              })}
              onupdatefiles={fileItems => {
                this.setState({
                  files: fileItems.map(fileItem => fileItem.file)
                })
                console.log(this.state.files);
              }}
              labelIdle='Drag & Drop your picture or <span class="filepond--label-action">Browse</span>'
            />
            <div className="mT-30">
              <form className="form-container" name="form">
                <div className="form-row">
                  <div className="form-group col-md-12">
                    <label htmlFor="meal_name">Meal Name</label>
                    <input type="text" className="form-control" name="meal_name" placeholder="Chicken Bacon Ranch Pizza" />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group col-md-12">
                    <label htmlFor="meal_desc">Meal Description</label>
                    <input type="text" className="form-control" name="meal_desc" placeholder="Asiago Chicken, Farm Raised Bacon, Hidden Valley Ranch, etc." />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label htmlFor="cost">Estimated Calories</label>
                    <input type="number" min="10" step="any" className="form-control" name="calories" placeholder="350" />
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="meal_cat1">Meal Category</label>
                    <select name="meal_cat" className="form-control">
                      <option>Choose...</option>
                      <option>American</option>
                      <option>Appetizers</option>
                      <option>Asian</option>
                      <option>Italian</option>
                      <option>Mexican</option>
                      <option>Salads</option>
                    </select>
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="locations">Location</label>
                    <select name="locations" id="locations" className="form-control">
                      <option>Choose...</option>
                    </select>
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label htmlFor="cost">Cost</label>
                    <input type="number" min="1" step="any" className="form-control" name="cost" placeholder="$3.50" />
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="cost">Default Net Weight (oz)</label>
                    <input type="number" max="100" step="any" className="form-control" name="weight" placeholder="4.21" />
                  </div>
                  <div className="form-group col-md-4">
                    <label htmlFor="shelf_life">Shelf Life (days)</label>
                    <input type="number" min="0.25" max="14" step="any" className="form-control" name="shelf_life" placeholder="2.5" />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group">
                    <div className="checkbox checkbox-circle checkbox-info peers ai-c">
                      <input type="checkbox" id="active" name="active" className="peer" onClick={() => { this.activeClicked() }} />
                      <label htmlFor="active" className="peers peer-greed js-sb ai-c">
                        <span className="peer peer-greed">Active</span>
                      </label>
                    </div>
                  </div>
                </div>
                <div id="activeUntil" hidden>
                  <div className="col-md-3">
                    <label className="fw-500">Active Until</label>
                    <div className="timepicker-input input-icon form-group">
                      <DatePicker
                        name="activeUntilDate"
                        selected={this.state.startDate}
                        onChange={this.datePickerChange}
                      />
                    </div>
                  </div>
                </div>
                <div className="form-row center full">
                  <button type="submit" className="col-md-12 form-row center btn btn-primary" onClick={() => { this.addNewMeal() }}>Add Meal to Inventory</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      );
    }
    else {
      return null;
    }
  }
}

function mapStateToProps(state) {
  const { authentication, data } = state;
  const { user, userData } = authentication;
  const { restaurantData } = data;
  return {
    user,
    userData,
    restaurantData
  };
}

const AddMeal = connect(mapStateToProps)(AddMealPage);
export default AddMeal;
